class ThailandSurprise {
	public $clothes;
	private $balls;
	private $secret_adventure;

	public function __construct($adventure) {
		$this->balls = rand(0, 1);
		return $this->secret_adventure = $adventure;
	}

	public function attachBalls() {
		return $this->balls = 1;
	}

	public function detachBalls() {
		return $this->balls = 0;
	}

	public function getPhoto() {
		if($this->balls) {
			return 'https://4.imimg.com/data4/WD/LH/MY-842422/plastic-balls-250x250.png';
		} else {
			return 'data:image/jpeg;base64,';
		}
	}
}

$surprise = new ThailandSurprise('Buvau Narnijoje');
var_dump($surprise);
?>
